package pkgshape;

public class Pentagon extends Shape {
	
	private double s1;
	private double s2;
	private double s3;
	private double s4;
	private double s5;
	
	@Override
	public double Area() {
		// TODO Auto-generated method stub
		return 0;
	}

	@Override
	public double Perimeter() {
		// TODO Auto-generated method stub
		return s1 + s2 + s3 + s4 + s5;
	}

	@Override
	public boolean AreSidesEqual() {
		// TODO Auto-generated method stub
		return false;
	}

}
